import React, { useState } from 'react';
import { useTranslation } from 'react-i18next';
import { motion } from 'framer-motion';
import { Helmet } from 'react-helmet-async';
import { 
  FiAward, 
  FiUsers, 
  FiBook, 
  FiTarget,
  FiEye,
  FiHeart,
  FiStar,
  FiTrendingUp,
  FiCalendar,
  FiMapPin,
  FiPhone,
  FiMail,
  FiGlobe,
  FiShield,
  FiCheckCircle,
  FiClock,
  FiImage,
  FiSettings,
  FiBarChart2
} from 'react-icons/fi';

const AboutPage = () => {
  const { t, i18n } = useTranslation();
  const currentLang = i18n.language;
  const [activeTab, setActiveTab] = useState('overview');

  // Comprehensive institutional data
  const institutionData = {
    establishment: {
      year: 1985,
      founder: currentLang === 'bn' ? 'প্রফেসর ড. আব্দুল করিম' : 'Professor Dr. Abdul Karim',
      location: currentLang === 'bn' ? 'ঢাকা, বাংলাদেশ' : 'Dhaka, Bangladesh'
    },
    statistics: [
      { 
        icon: FiCalendar, 
        number: '38+', 
        label: currentLang === 'bn' ? 'বছরের অভিজ্ঞতা' : 'Years of Excellence',
        color: 'text-blue-600'
      },
      { 
        icon: FiUsers, 
        number: '3,250+', 
        label: currentLang === 'bn' ? 'শিক্ষার্থী' : 'Students',
        color: 'text-green-600'
      },
      { 
        icon: FiUsers, 
        number: '185+', 
        label: currentLang === 'bn' ? 'শিক্ষক ও কর্মচারী' : 'Faculty & Staff',
        color: 'text-purple-600'
      },
      { 
        icon: FiAward, 
        number: '98%', 
        label: currentLang === 'bn' ? 'সফলতার হার' : 'Success Rate',
        color: 'text-orange-600'
      },
      { 
        icon: FiStar, 
        number: '4.9/5', 
        label: currentLang === 'bn' ? 'অভিভাবক রেটিং' : 'Parent Rating',
        color: 'text-red-600'
      },
      { 
        icon: FiTrendingUp, 
        number: '15,000+', 
        label: currentLang === 'bn' ? 'স্নাতক' : 'Alumni',
        color: 'text-indigo-600'
      }
    ],
    accreditation: [
      {
        title: currentLang === 'bn' ? 'মাধ্যমিক ও উচ্চ মাধ্যমিক শিক্ষা বোর্ড' : 'Secondary & Higher Secondary Education Board',
        status: currentLang === 'bn' ? 'অনুমোদিত' : 'Approved',
        year: '1985'
      },
      {
        title: currentLang === 'bn' ? 'শিক্ষা মন্ত্রণালয়, বাংলাদেশ সরকার' : 'Ministry of Education, Government of Bangladesh',
        status: currentLang === 'bn' ? 'স্বীকৃত' : 'Recognized',
        year: '1987'
      },
      {
        title: currentLang === 'bn' ? 'এমপিও অনুমোদন' : 'MPO Approval',
        status: currentLang === 'bn' ? 'অনুমোদিত' : 'Approved',
        year: '1990'
      }
    ]
  };

  // Academic programs data
  const academicPrograms = [
    {
      level: currentLang === 'bn' ? 'মাধ্যমিক (৬ষ্ঠ-১০ম শ্রেণি)' : 'Secondary (Class 6-10)',
      subjects: currentLang === 'bn' ? 'বাংলা, ইংরেজি, গণিত, বিজ্ঞান, সামাজিক বিজ্ঞান, ধর্ম' : 'Bangla, English, Mathematics, Science, Social Science, Religion',
      students: '1,850'
    },
    {
      level: currentLang === 'bn' ? 'উচ্চ মাধ্যমিক (১১শ-১২শ শ্রেণি)' : 'Higher Secondary (Class 11-12)',
      subjects: currentLang === 'bn' ? 'বিজ্ঞান, ব্যবসায় শিক্ষা, মানবিক' : 'Science, Business Studies, Humanities',
      students: '1,400'
    }
  ];

  // Management committee data
  const managementCommittee = [
    {
      name: currentLang === 'bn' ? 'জনাব মোহাম্মদ রহিম উদ্দিন' : 'Mr. Mohammad Rahim Uddin',
      position: currentLang === 'bn' ? 'সভাপতি' : 'President',
      qualification: currentLang === 'bn' ? 'এম.এ, এল.এল.বি' : 'M.A, LL.B'
    },
    {
      name: currentLang === 'bn' ? 'প্রফেসর ড. ফাতেমা খাতুন' : 'Professor Dr. Fatema Khatun',
      position: currentLang === 'bn' ? 'প্রধান শিক্ষক' : 'Head Teacher',
      qualification: currentLang === 'bn' ? 'এম.এড, পিএইচ.ডি' : 'M.Ed, Ph.D'
    },
    {
      name: currentLang === 'bn' ? 'জনাব আব্দুল করিম' : 'Mr. Abdul Karim',
      position: currentLang === 'bn' ? 'সহ-সভাপতি' : 'Vice President',
      qualification: currentLang === 'bn' ? 'এম.কম' : 'M.Com'
    }
  ];

  // Infrastructure and facilities
  const facilities = [
    {
      icon: FiUsers,
      title: { en: 'Expert Faculty', bn: 'দক্ষ শিক্ষকমণ্ডলী' },
      description: { 
        en: 'Highly qualified and experienced teachers committed to student success.',
        bn: 'শিক্ষার্থীদের সাফল্যের জন্য প্রতিশ্রুতিবদ্ধ উচ্চ যোগ্যতাসম্পন্ন এবং অভিজ্ঞ শিক্ষকবৃন্দ।'
      }
    },
    {
      icon: FiTrendingUp,
      title: { en: 'Modern Facilities', bn: 'আধুনিক সুবিধা' },
      description: { 
        en: 'State-of-the-art infrastructure and learning resources for holistic development.',
        bn: 'সর্বাঙ্গীণ উন্নয়নের জন্য অত্যাধুনিক অবকাঠামো এবং শিক্ষা সম্পদ।'
      }
    },
    {
      icon: FiHeart,
      title: { en: 'Character Building', bn: 'চরিত্র গঠন' },
      description: { 
        en: 'Focus on moral values, ethics, and personality development alongside academics.',
        bn: 'একাডেমিকের পাশাপাশি নৈতিক মূল্যবোধ, নীতিশাস্ত্র এবং ব্যক্তিত্ব বিকাশে গুরুত্ব।'
      }
    }
  ];

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900">
      <Helmet>
        <title>{t('navigation.about')} - School Management System</title>
        <meta name="description" content="Learn about our school's history, mission, vision, and commitment to educational excellence" />
      </Helmet>

      {/* Hero Section */}
      <section className="relative py-20 bg-gradient-to-br from-primary-600 to-primary-800 text-white overflow-hidden">
        <div className="absolute inset-0 bg-black opacity-20"></div>
        <div className="container-custom relative z-10">
          <motion.div
            initial={{ y: 50, opacity: 0 }}
            animate={{ y: 0, opacity: 1 }}
            transition={{ duration: 0.8 }}
            className="text-center max-w-4xl mx-auto"
          >
            <h1 className="text-4xl md:text-6xl font-bold mb-6">
              {t('navigation.about')} Our School
            </h1>
            <p className="text-xl md:text-2xl text-primary-100 leading-relaxed">
              Nurturing minds, building futures, and creating tomorrow's leaders through excellence in education
            </p>
          </motion.div>
        </div>
      </section>

      {/* Achievements Section */}
      <section className="py-16 bg-white dark:bg-gray-800">
        <div className="container-custom">
          <div className="grid grid-cols-2 md:grid-cols-4 gap-8">
            {achievements.map((achievement, index) => {
              const Icon = achievement.icon;
              return (
                <motion.div
                  key={index}
                  initial={{ y: 50, opacity: 0 }}
                  whileInView={{ y: 0, opacity: 1 }}
                  transition={{ delay: index * 0.1, duration: 0.6 }}
                  viewport={{ once: true }}
                  className="text-center"
                >
                  <div className="inline-flex items-center justify-center w-16 h-16 bg-primary-100 dark:bg-primary-900 text-primary-600 dark:text-primary-400 rounded-full mb-4">
                    <Icon className="w-8 h-8" />
                  </div>
                  <h3 className="text-3xl font-bold text-gray-900 dark:text-white mb-2">
                    {achievement.number}
                  </h3>
                  <p className="text-gray-600 dark:text-gray-400">
                    {achievement.label}
                  </p>
                </motion.div>
              );
            })}
          </div>
        </div>
      </section>

      {/* Mission & Vision */}
      <section className="py-16 bg-gray-50 dark:bg-gray-900">
        <div className="container-custom">
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-12">
            <motion.div
              initial={{ x: -50, opacity: 0 }}
              whileInView={{ x: 0, opacity: 1 }}
              transition={{ duration: 0.8 }}
              viewport={{ once: true }}
              className="bg-white dark:bg-gray-800 rounded-xl shadow-soft p-8"
            >
              <div className="flex items-center mb-6">
                <div className="w-12 h-12 bg-primary-100 dark:bg-primary-900 text-primary-600 dark:text-primary-400 rounded-lg flex items-center justify-center mr-4">
                  <FiTarget className="w-6 h-6" />
                </div>
                <h2 className="text-2xl font-bold text-gray-900 dark:text-white">
                  {t('institution.mission')}
                </h2>
              </div>
              <p className="text-gray-600 dark:text-gray-400 leading-relaxed">
                To provide quality education that empowers students with knowledge, skills, and values necessary for success in life. We are committed to fostering an environment that encourages critical thinking, creativity, and character development while preparing students to become responsible global citizens.
              </p>
            </motion.div>

            <motion.div
              initial={{ x: 50, opacity: 0 }}
              whileInView={{ x: 0, opacity: 1 }}
              transition={{ duration: 0.8 }}
              viewport={{ once: true }}
              className="bg-white dark:bg-gray-800 rounded-xl shadow-soft p-8"
            >
              <div className="flex items-center mb-6">
                <div className="w-12 h-12 bg-primary-100 dark:bg-primary-900 text-primary-600 dark:text-primary-400 rounded-lg flex items-center justify-center mr-4">
                  <FiEye className="w-6 h-6" />
                </div>
                <h2 className="text-2xl font-bold text-gray-900 dark:text-white">
                  {t('institution.vision')}
                </h2>
              </div>
              <p className="text-gray-600 dark:text-gray-400 leading-relaxed">
                To be a leading educational institution recognized for academic excellence, innovative teaching methods, and holistic development of students. We envision creating future leaders who will contribute positively to society and make a meaningful impact in their chosen fields.
              </p>
            </motion.div>
          </div>
        </div>
      </section>

      {/* Features Section */}
      <section className="py-16 bg-white dark:bg-gray-800">
        <div className="container-custom">
          <motion.div
            initial={{ y: 50, opacity: 0 }}
            whileInView={{ y: 0, opacity: 1 }}
            transition={{ duration: 0.6 }}
            viewport={{ once: true }}
            className="text-center mb-12"
          >
            <h2 className="text-3xl md:text-4xl font-bold text-gray-900 dark:text-white mb-4">
              Why Choose Our School?
            </h2>
            <p className="text-xl text-gray-600 dark:text-gray-400 max-w-2xl mx-auto">
              We provide a comprehensive educational experience that goes beyond academics
            </p>
          </motion.div>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
            {features.map((feature, index) => {
              const Icon = feature.icon;
              return (
                <motion.div
                  key={index}
                  initial={{ y: 50, opacity: 0 }}
                  whileInView={{ y: 0, opacity: 1 }}
                  transition={{ delay: index * 0.1, duration: 0.6 }}
                  viewport={{ once: true }}
                  className="flex items-start space-x-4 p-6 bg-gray-50 dark:bg-gray-700 rounded-xl hover:shadow-soft transition-shadow duration-300"
                >
                  <div className="flex-shrink-0 w-12 h-12 bg-primary-100 dark:bg-primary-900 text-primary-600 dark:text-primary-400 rounded-lg flex items-center justify-center">
                    <Icon className="w-6 h-6" />
                  </div>
                  <div>
                    <h3 className="text-xl font-semibold text-gray-900 dark:text-white mb-2">
                      {feature.title[currentLang] || feature.title.en}
                    </h3>
                    <p className="text-gray-600 dark:text-gray-400 leading-relaxed">
                      {feature.description[currentLang] || feature.description.en}
                    </p>
                  </div>
                </motion.div>
              );
            })}
          </div>
        </div>
      </section>

      {/* History Section */}
      <section className="py-16 bg-gray-50 dark:bg-gray-900">
        <div className="container-custom">
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-12 items-center">
            <motion.div
              initial={{ x: -50, opacity: 0 }}
              whileInView={{ x: 0, opacity: 1 }}
              transition={{ duration: 0.8 }}
              viewport={{ once: true }}
            >
              <h2 className="text-3xl md:text-4xl font-bold text-gray-900 dark:text-white mb-6">
                {t('institution.history')}
              </h2>
              <div className="space-y-4 text-gray-600 dark:text-gray-400 leading-relaxed">
                <p>
                  Established in 1998, ABC School & College has been a beacon of educational excellence for over two decades. What started as a small institution with a handful of students has grown into one of the most respected educational establishments in the region.
                </p>
                <p>
                  Our journey began with a simple yet powerful vision: to provide quality education that transforms lives and builds character. Over the years, we have consistently maintained our commitment to academic excellence while adapting to modern educational needs and technologies.
                </p>
                <p>
                  Today, we proudly serve over 2,500 students from diverse backgrounds, supported by a dedicated team of 150+ qualified teachers and staff members. Our alumni have gone on to achieve success in various fields, making significant contributions to society.
                </p>
              </div>
            </motion.div>

            <motion.div
              initial={{ x: 50, opacity: 0 }}
              whileInView={{ x: 0, opacity: 1 }}
              transition={{ duration: 0.8 }}
              viewport={{ once: true }}
              className="relative"
            >
              <div className="bg-primary-600 rounded-xl p-8 text-white">
                <h3 className="text-2xl font-bold mb-6">Key Milestones</h3>
                <div className="space-y-4">
                  <div className="flex items-center space-x-3">
                    <div className="w-3 h-3 bg-white rounded-full flex-shrink-0"></div>
                    <div>
                      <p className="font-semibold">1998</p>
                      <p className="text-primary-100">School established with 50 students</p>
                    </div>
