import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useTranslation } from 'react-i18next';
import { motion } from 'framer-motion';
import { Helmet } from 'react-helmet-async';
import { 
  FiSearch, 
  FiFilter, 
  FiUsers, 
  FiTrendingUp,
  FiAward,
  FiCalendar,
  FiUser,
  FiBook,
  FiBarChart2,
  FiPieChart
} from 'react-icons/fi';

// Redux actions
import { fetchStudents, setFilters, clearFilters } from '../store/slices/studentSlice';

// Components
import LoadingSpinner from '../components/common/LoadingSpinner';

const StudentsPage = () => {
  const dispatch = useDispatch();
  const { t, i18n } = useTranslation();

  const { students, loading, error, filters } = useSelector(state => state.students);
  const [searchTerm, setSearchTerm] = useState(filters.search || '');
  const [showFilters, setShowFilters] = useState(false);
  const [viewMode, setViewMode] = useState('statistics'); // 'statistics' or 'list'

  // Mock student statistics data
  const studentStats = {
    totalStudents: 2450,
    classWiseData: [
      { class: 'Play', male: 45, female: 38, total: 83 },
      { class: 'Nursery', male: 52, female: 48, total: 100 },
      { class: 'KG', male: 58, female: 55, total: 113 },
      { class: 'Class 1', male: 85, female: 78, total: 163 },
      { class: 'Class 2', male: 92, female: 88, total: 180 },
      { class: 'Class 3', male: 98, female: 95, total: 193 },
      { class: 'Class 4', male: 105, female: 98, total: 203 },
      { class: 'Class 5', male: 110, female: 102, total: 212 },
      { class: 'Class 6', male: 115, female: 108, total: 223 },
      { class: 'Class 7', male: 118, female: 112, total: 230 },
      { class: 'Class 8', male: 122, female: 115, total: 237 },
      { class: 'Class 9', male: 125, female: 118, total: 243 },
      { class: 'Class 10', male: 128, female: 122, total: 250 },
      { class: 'HSC 1st Year', male: 135, female: 125, total: 260 },
      { class: 'HSC 2nd Year', male: 132, female: 128, total: 260 }
    ],
    genderDistribution: {
      male: 1320,
      female: 1130,
      total: 2450
    },
    groupWiseData: {
      'Science': { male: 180, female: 165, total: 345 },
      'Commerce': { male: 95, female: 110, total: 205 },
      'Arts': { male: 75, female: 85, total: 160 }
    },
    academicYear: '2024-2025',
    achievements: [
      { title: 'SSC Pass Rate 2024', value: '98.5%', color: 'text-green-600' },
      { title: 'HSC Pass Rate 2024', value: '96.2%', color: 'text-blue-600' },
      { title: 'GPA 5.00 (SSC)', value: '45 Students', color: 'text-purple-600' },
      { title: 'GPA 5.00 (HSC)', value: '38 Students', color: 'text-yellow-600' }
    ]
  };

  const handleSearch = (e) => {
    e.preventDefault();
    // Search functionality would be implemented here
  };

  const handleFilterChange = (key, value) => {
    dispatch(setFilters({ [key]: value }));
  };

  const clearAllFilters = () => {
    setSearchTerm('');
    dispatch(clearFilters());
  };

  const getClassColor = (className) => {
    const colors = {
      'Play': 'bg-pink-100 text-pink-800 dark:bg-pink-900 dark:text-pink-300',
      'Nursery': 'bg-purple-100 text-purple-800 dark:bg-purple-900 dark:text-purple-300',
      'KG': 'bg-indigo-100 text-indigo-800 dark:bg-indigo-900 dark:text-indigo-300',
      'Class 1': 'bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-300',
      'Class 2': 'bg-cyan-100 text-cyan-800 dark:bg-cyan-900 dark:text-cyan-300',
      'Class 3': 'bg-teal-100 text-teal-800 dark:bg-teal-900 dark:text-teal-300',
      'Class 4': 'bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-300',
      'Class 5': 'bg-lime-100 text-lime-800 dark:bg-lime-900 dark:text-lime-300',
      'Class 6': 'bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-300',
      'Class 7': 'bg-orange-100 text-orange-800 dark:bg-orange-900 dark:text-orange-300',
      'Class 8': 'bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-300',
      'Class 9': 'bg-rose-100 text-rose-800 dark:bg-rose-900 dark:text-rose-300',
      'Class 10': 'bg-violet-100 text-violet-800 dark:bg-violet-900 dark:text-violet-300',
      'HSC 1st Year': 'bg-emerald-100 text-emerald-800 dark:bg-emerald-900 dark:text-emerald-300',
      'HSC 2nd Year': 'bg-sky-100 text-sky-800 dark:bg-sky-900 dark:text-sky-300'
    };
    return colors[className] || 'bg-gray-100 text-gray-800 dark:bg-gray-700 dark:text-gray-300';
  };

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900">
      <Helmet>
        <title>{t('students.students')} - School Management System</title>
        <meta name="description" content="Student information, statistics, and academic achievements" />
      </Helmet>

      <div className="container-custom py-8">
        {/* Header */}
        <motion.div
          initial={{ y: -20, opacity: 0 }}
          animate={{ y: 0, opacity: 1 }}
          transition={{ duration: 0.6 }}
          className="text-center mb-8"
        >
          <h1 className="text-4xl font-bold text-gray-900 dark:text-white mb-4">
            {t('students.students')} Information
          </h1>
          <p className="text-xl text-gray-600 dark:text-gray-400 max-w-2xl mx-auto">
            Comprehensive overview of our student body, achievements, and academic statistics
          </p>
        </motion.div>

        {/* View Mode Toggle */}
        <motion.div
          initial={{ y: 20, opacity: 0 }}
          animate={{ y: 0, opacity: 1 }}
          transition={{ delay: 0.2, duration: 0.6 }}
          className="flex justify-center mb-8"
        >
          <div className="bg-white dark:bg-gray-800 rounded-lg shadow-soft p-1">
            <button
              onClick={() => setViewMode('statistics')}
              className={`px-6 py-2 rounded-md text-sm font-medium transition-colors ${
                viewMode === 'statistics'
                  ? 'bg-primary-600 text-white'
                  : 'text-gray-600 dark:text-gray-400 hover:text-gray-900 dark:hover:text-gray-100'
              }`}
            >
              <FiBarChart3 className="w-4 h-4 mr-2 inline" />
              Statistics
            </button>
            <button
              onClick={() => setViewMode('list')}
              className={`px-6 py-2 rounded-md text-sm font-medium transition-colors ${
                viewMode === 'list'
                  ? 'bg-primary-600 text-white'
                  : 'text-gray-600 dark:text-gray-400 hover:text-gray-900 dark:hover:text-gray-100'
              }`}
            >
              <FiUsers className="w-4 h-4 mr-2 inline" />
              Class List
            </button>
          </div>
        </motion.div>

        {viewMode === 'statistics' ? (
          <>
            {/* Overall Statistics */}
            <motion.div
              initial={{ y: 20, opacity: 0 }}
              animate={{ y: 0, opacity: 1 }}
              transition={{ delay: 0.3, duration: 0.6 }}
              className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8"
            >
              <div className="bg-white dark:bg-gray-800 rounded-xl shadow-soft p-6 text-center">
                <div className="w-12 h-12 bg-blue-100 dark:bg-blue-900 text-blue-600 dark:text-blue-400 rounded-lg flex items-center justify-center mx-auto mb-4">
                  <FiUsers className="w-6 h-6" />
                </div>
                <h3 className="text-3xl font-bold text-gray-900 dark:text-white mb-2">
                  {studentStats.totalStudents.toLocaleString()}
                </h3>
                <p className="text-gray-600 dark:text-gray-400">Total Students</p>
              </div>

              <div className="bg-white dark:bg-gray-800 rounded-xl shadow-soft p-6 text-center">
                <div className="w-12 h-12 bg-green-100 dark:bg-green-900 text-green-600 dark:text-green-400 rounded-lg flex items-center justify-center mx-auto mb-4">
                  <FiUser className="w-6 h-6" />
                </div>
                <h3 className="text-3xl font-bold text-gray-900 dark:text-white mb-2">
                  {studentStats.genderDistribution.male}
                </h3>
                <p className="text-gray-600 dark:text-gray-400">Male Students</p>
              </div>

              <div className="bg-white dark:bg-gray-800 rounded-xl shadow-soft p-6 text-center">
                <div className="w-12 h-12 bg-pink-100 dark:bg-pink-900 text-pink-600 dark:text-pink-400 rounded-lg flex items-center justify-center mx-auto mb-4">
                  <FiUser className="w-6 h-6" />
                </div>
                <h3 className="text-3xl font-bold text-gray-900 dark:text-white mb-2">
                  {studentStats.genderDistribution.female}
                </h3>
                <p className="text-gray-600 dark:text-gray-400">Female Students</p>
              </div>

              <div className="bg-white dark:bg-gray-800 rounded-xl shadow-soft p-6 text-center">
                <div className="w-12 h-12 bg-purple-100 dark:bg-purple-900 text-purple-600 dark:text-purple-400 rounded-lg flex items-center justify-center mx-auto mb-4">
                  <FiCalendar className="w-6 h-6" />
                </div>
                <h3 className="text-xl font-bold text-gray-900 dark:text-white mb-2">
                  {studentStats.academicYear}
                </h3>
                <p className="text-gray-600 dark:text-gray-400">Academic Year</p>
              </div>
            </motion.div>

            {/* Class-wise Distribution */}
            <motion.div
              initial={{ y: 20, opacity: 0 }}
              animate={{ y: 0, opacity: 1 }}
              transition={{ delay: 0.4, duration: 0.6 }}
              className="bg-white dark:bg-gray-800 rounded-xl shadow-soft p-6 mb-8"
            >
              <h2 className="text-2xl font-bold text-gray-900 dark:text-white mb-6">
                Class-wise Student Distribution
              </h2>
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                {studentStats.classWiseData.map((classData, index) => (
                  <motion.div
                    key={classData.class}
                    initial={{ scale: 0.9, opacity: 0 }}
                    animate={{ scale: 1, opacity: 1 }}
                    transition={{ delay: index * 0.05, duration: 0.3 }}
                    className="bg-gray-50 dark:bg-gray-700 rounded-lg p-4 hover:shadow-md transition-shadow"
                  >
                    <div className="flex items-center justify-between mb-3">
                      <span className={`badge ${getClassColor(classData.class)}`}>
                        {classData.class}
                      </span>
                      <span className="text-lg font-bold text-gray-900 dark:text-white">
                        {classData.total}
                      </span>
                    </div>
                    <div className="flex justify-between text-sm text-gray-600 dark:text-gray-400">
                      <span>Male: {classData.male}</span>
                      <span>Female: {classData.female}</span>
                    </div>
                    <div className="mt-2 bg-gray-200 dark:bg-gray-600 rounded-full h-2">
                      <div 
                        className="bg-primary-600 h-2 rounded-full"
                        style={{ width: `${(classData.total / 260) * 100}%` }}
                      ></div>
                    </div>
                  </motion.div>
                ))}
              </div>
            </motion.div>

            {/* Group-wise Distribution (HSC) */}
            <motion.div
              initial={{ y: 20, opacity: 0 }}
              animate={{ y: 0, opacity: 1 }}
              transition={{ delay: 0.5, duration: 0.6 }}
              className="bg-white dark:bg-gray-800 rounded-xl shadow-soft p-6 mb-8"
            >
              <h2 className="text-2xl font-bold text-gray-900 dark:text-white mb-6">
                HSC Group-wise Distribution
              </h2>
              <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                {Object.entries(studentStats.groupWiseData).map(([group, data], index) => (
                  <motion.div
                    key={group}
                    initial={{ y: 20, opacity: 0 }}
                    animate={{ y: 0, opacity: 1 }}
                    transition={{ delay: index * 0.1, duration: 0.6 }}
                    className="text-center p-6 bg-gray-50 dark:bg-gray-700 rounded-lg"
                  >
                    <div className={`w-16 h-16 mx-auto mb-4 rounded-full flex items-center justify-center ${
                      group === 'Science' ? 'bg-blue-100 text-blue-600 dark:bg-blue-900 dark:text-blue-400' :
                      group === 'Commerce' ? 'bg-green-100 text-green-600 dark:bg-green-900 dark:text-green-400' :
                      'bg-purple-100 text-purple-600 dark:bg-purple-900 dark:text-purple-400'
                    }`}>
                      <FiBook className="w-8 h-8" />
                    </div>
                    <h3 className="text-xl font-bold text-gray-900 dark:text-white mb-2">
                      {group}
                    </h3>
                    <p className="text-2xl font-bold text-primary-600 dark:text-primary-400 mb-2">
                      {data.total}
                    </p>
                    <div className="text-sm text-gray-600 dark:text-gray-400">
                      <p>Male: {data.male}</p>
                      <p>Female: {data.female}</p>
                    </div>
                  </motion.div>
                ))}
              </div>
            </motion.div>

            {/* Academic Achievements */}
            <motion.div
              initial={{ y: 20, opacity: 0 }}
              animate={{ y: 0, opacity: 1 }}
              transition={{ delay: 0.6, duration: 0.6 }}
              className="bg-white dark:bg-gray-800 rounded-xl shadow-soft p-6"
            >
              <h2 className="text-2xl font-bold text-gray-900 dark:text-white mb-6">
                Academic Achievements
              </h2>
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
                {studentStats.achievements.map((achievement, index) => (
                  <motion.div
                    key={achievement.title}
                    initial={{ scale: 0.9, opacity: 0 }}
                    animate={{ scale: 1, opacity: 1 }}
                    transition={{ delay: index * 0.1, duration: 0.6 }}
                    className="text-center p-4 bg-gray-50 dark:bg-gray-700 rounded-lg"
                  >
                    <div className="w-12 h-12 bg-yellow-100 dark:bg-yellow-900 text-yellow-600 dark:text-yellow-400 rounded-lg flex items-center justify-center mx-auto mb-3">
                      <FiAward className="w-6 h-6" />
                    </div>
                    <h3 className="text-sm font-medium text-gray-600 dark:text-gray-400 mb-2">
                      {achievement.title}
                    </h3>
                    <p className={`text-2xl font-bold ${achievement.color} dark:opacity-80`}>
                      {achievement.value}
                    </p>
                  </motion.div>
                ))}
              </div>
            </motion.div>
          </>
        ) : (
          /* Class List View */
          <motion.div
            initial={{ y: 20, opacity: 0 }}
            animate={{ y: 0, opacity: 1 }}
            transition={{ delay: 0.3, duration: 0.6 }}
            className="bg-white dark:bg-gray-800 rounded-xl shadow-soft p-6"
          >
            {/* Search and Filters */}
            <div className="mb-6">
              <form onSubmit={handleSearch} className="flex gap-4 mb-4">
                <div className="flex-1 relative">
                  <FiSearch className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-5 h-5" />
                  <input
                    type="text"
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    placeholder="Search students by name, class, or ID..."
                    className="form-input pl-10 w-full"
                  />
                </div>
                <button
                  type="submit"
                  className="btn btn-primary px-6"
                >
                  {t('common.search')}
                </button>
                <button
                  type="button"
                  onClick={() => setShowFilters(!showFilters)}
                  className="btn btn-outline px-6"
                >
                  <FiFilter className="w-5 h-5 mr-2" />
                  {t('common.filter')}
                </button>
              </form>

              {showFilters && (
                <motion.div
                  initial={{ height: 0, opacity: 0 }}
                  animate={{ height: 'auto', opacity: 1 }}
                  exit={{ height: 0, opacity: 0 }}
                  className="border-t border-gray-200 dark:border-gray-700 pt-4"
                >
                  <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                    <div>
                      <label className="form-label">Class</label>
                      <select
                        value={filters.class}
                        onChange={(e) => handleFilterChange('class', e.target.value)}
                        className="form-input"
                      >
                        <option value="">All Classes</option>
                        {studentStats.classWiseData.map(classData => (
                          <option key={classData.class} value={classData.class}>
                            {classData.class}
                          </option>
                        ))}
                      </select>
                    </div>

                    <div>
                      <label className="form-label">Section</label>
                      <select
                        value={filters.section}
                        onChange={(e) => handleFilterChange('section', e.target.value)}
                        className="form-input"
                      >
                        <option value="">All Sections</option>
                        <option value="A">A</option>
                        <option value="B">B</option>
                        <option value="C">C</option>
                      </select>
                    </div>

                    <div className="flex items-end">
                      <button
                        onClick={clearAllFilters}
                        className="btn btn-ghost w-full"
                      >
                        {t('common.clear')} {t('common.filter')}
                      </button>
                    </div>
                  </div>
                </motion.div>
              )}
            </div>

            {/* Student List Placeholder */}
            <div className="text-center py-12">
              <FiUsers className="w-16 h-16 text-gray-400 mx-auto mb-4" />
              <h3 className="text-xl font-semibold text-gray-900 dark:text-white mb-2">
                Student Database
              </h3>
              <p className="text-gray-600 dark:text-gray-400 mb-4">
                Detailed student information with search and filter capabilities
              </p>
              <p className="text-sm text-gray-500 dark:text-gray-400">
                This feature requires authentication and proper permissions to view individual student records.
              </p>
            </div>
          </motion.div>
        )}
      </div>
    </div>
  );
};

export default StudentsPage;
