import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';

const initialState = {
  students: [],
  currentStudent: null,
  loading: false,
  error: null,
  pagination: { currentPage: 1, totalPages: 1, total: 0, limit: 10 },
  filters: { class: '', section: '', search: '' },
};

export const fetchStudents = createAsyncThunk(
  'students/fetchStudents',
  async (params = {}, { rejectWithValue }) => {
    try {
      // Placeholder - will implement API call
      return { students: [], total: 0, currentPage: 1, totalPages: 1 };
    } catch (error) {
      return rejectWithValue('Failed to fetch students');
    }
  }
);

const studentSlice = createSlice({
  name: 'students',
  initialState,
  reducers: {
    clearError: (state) => { state.error = null; },
    setFilters: (state, action) => { state.filters = { ...state.filters, ...action.payload }; },
  },
  extraReducers: (builder) => {
    builder
      .addCase(fetchStudents.pending, (state) => { state.loading = true; })
      .addCase(fetchStudents.fulfilled, (state, action) => {
        state.loading = false;
        state.students = action.payload.students || [];
      })
      .addCase(fetchStudents.rejected, (state, action) => {
        state.loading = false;
        state.error = action.payload;
      });
  },
});

export const { clearError, setFilters } = studentSlice.actions;
export default studentSlice.reducer;
