import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useTranslation } from 'react-i18next';
import { motion } from 'framer-motion';
import { Helmet } from 'react-helmet-async';
import { 
  FiSearch, 
  FiFilter, 
  FiMail, 
  FiPhone, 
  FiMapPin,
  FiBook,
  FiAward,
  FiCalendar,
  FiUser
} from 'react-icons/fi';

// Redux actions
import { fetchTeachers, setFilters, clearFilters } from '../store/slices/teacherSlice';

// Components
import LoadingSpinner from '../components/common/LoadingSpinner';

const TeachersPage = () => {
  const dispatch = useDispatch();
  const { t, i18n } = useTranslation();

  const { teachers, loading, error, filters } = useSelector(state => state.teachers);
  const [searchTerm, setSearchTerm] = useState(filters.search || '');
  const [showFilters, setShowFilters] = useState(false);

  // Mock teacher data (replace with real API data)
  const mockTeachers = [
    {
      _id: '1',
      user: {
        name: 'Dr. Mohammad Rahman',
        email: 'rahman@school.edu',
        phone: '+880-1234-567890',
        avatar: { url: null }
      },
      teacherId: 'TCH001',
      designation: 'Head Teacher',
      department: 'Administration',
      subjects: ['Educational Leadership', 'School Management'],
      qualifications: [
        { degree: 'Ph.D in Education', institution: 'University of Dhaka', year: 2010 },
        { degree: 'M.Ed', institution: 'University of Dhaka', year: 2005 }
      ],
      experience: { totalYears: 25 },
      joiningDate: '1998-01-15',
      personalInfo: {
        dateOfBirth: '1970-05-20',
        gender: 'Male'
      }
    },
    {
      _id: '2',
      user: {
        name: 'Prof. Fatima Khatun',
        email: 'fatima@school.edu',
        phone: '+880-1234-567891',
        avatar: { url: null }
      },
      teacherId: 'TCH002',
      designation: 'Senior Teacher',
      department: 'Mathematics',
      subjects: ['Higher Mathematics', 'Statistics'],
      qualifications: [
        { degree: 'M.Sc in Mathematics', institution: 'University of Dhaka', year: 2008 },
        { degree: 'B.Sc in Mathematics', institution: 'University of Dhaka', year: 2006 }
      ],
      experience: { totalYears: 15 },
      joiningDate: '2008-03-01',
      personalInfo: {
        dateOfBirth: '1985-08-15',
        gender: 'Female'
      }
    },
    {
      _id: '3',
      user: {
        name: 'Mr. Abdul Karim',
        email: 'karim@school.edu',
        phone: '+880-1234-567892',
        avatar: { url: null }
      },
      teacherId: 'TCH003',
      designation: 'Assistant Teacher',
      department: 'English',
      subjects: ['English Literature', 'English Grammar'],
      qualifications: [
        { degree: 'M.A in English', institution: 'University of Dhaka', year: 2012 },
        { degree: 'B.A in English', institution: 'University of Dhaka', year: 2010 }
      ],
      experience: { totalYears: 12 },
      joiningDate: '2012-07-01',
      personalInfo: {
        dateOfBirth: '1988-12-10',
        gender: 'Male'
      }
    },
    {
      _id: '4',
      user: {
        name: 'Ms. Rashida Begum',
        email: 'rashida@school.edu',
        phone: '+880-1234-567893',
        avatar: { url: null }
      },
      teacherId: 'TCH004',
      designation: 'Assistant Teacher',
      department: 'Science',
      subjects: ['Physics', 'Chemistry'],
      qualifications: [
        { degree: 'M.Sc in Physics', institution: 'University of Dhaka', year: 2015 },
        { degree: 'B.Sc in Physics', institution: 'University of Dhaka', year: 2013 }
      ],
      experience: { totalYears: 8 },
      joiningDate: '2015-01-15',
      personalInfo: {
        dateOfBirth: '1990-03-25',
        gender: 'Female'
      }
    },
    {
      _id: '5',
      user: {
        name: 'Mr. Aminul Islam',
        email: 'aminul@school.edu',
        phone: '+880-1234-567894',
        avatar: { url: null }
      },
      teacherId: 'TCH005',
      designation: 'Assistant Teacher',
      department: 'Social Science',
      subjects: ['History', 'Geography', 'Civics'],
      qualifications: [
        { degree: 'M.A in History', institution: 'University of Dhaka', year: 2014 },
        { degree: 'B.A in History', institution: 'University of Dhaka', year: 2012 }
      ],
      experience: { totalYears: 9 },
      joiningDate: '2014-08-01',
      personalInfo: {
        dateOfBirth: '1989-07-18',
        gender: 'Male'
      }
    },
    {
      _id: '6',
      user: {
        name: 'Mrs. Salma Akter',
        email: 'salma@school.edu',
        phone: '+880-1234-567895',
        avatar: { url: null }
      },
      teacherId: 'TCH006',
      designation: 'Assistant Teacher',
      department: 'Bangla',
      subjects: ['Bangla Literature', 'Bangla Grammar'],
      qualifications: [
        { degree: 'M.A in Bangla', institution: 'University of Dhaka', year: 2011 },
        { degree: 'B.A in Bangla', institution: 'University of Dhaka', year: 2009 }
      ],
      experience: { totalYears: 13 },
      joiningDate: '2011-02-15',
      personalInfo: {
        dateOfBirth: '1987-11-30',
        gender: 'Female'
      }
    }
  ];

  const [displayTeachers, setDisplayTeachers] = useState(mockTeachers);

  useEffect(() => {
    // Filter teachers based on search and filters
    let filtered = mockTeachers;

    if (searchTerm) {
      filtered = filtered.filter(teacher => 
        teacher.user.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
        teacher.department.toLowerCase().includes(searchTerm.toLowerCase()) ||
        teacher.designation.toLowerCase().includes(searchTerm.toLowerCase()) ||
        teacher.subjects.some(subject => subject.toLowerCase().includes(searchTerm.toLowerCase()))
      );
    }

    if (filters.department) {
      filtered = filtered.filter(teacher => teacher.department === filters.department);
    }

    if (filters.designation) {
      filtered = filtered.filter(teacher => teacher.designation === filters.designation);
    }

    setDisplayTeachers(filtered);
  }, [searchTerm, filters]);

  const handleSearch = (e) => {
    e.preventDefault();
    // Search is handled by useEffect
  };

  const handleFilterChange = (key, value) => {
    dispatch(setFilters({ [key]: value }));
  };

  const clearAllFilters = () => {
    setSearchTerm('');
    dispatch(clearFilters());
  };

  const getDesignationColor = (designation) => {
    const colors = {
      'Head Teacher': 'bg-purple-100 text-purple-800 dark:bg-purple-900 dark:text-purple-300',
      'Senior Teacher': 'bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-300',
      'Assistant Teacher': 'bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-300',
      'Junior Teacher': 'bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-300'
    };
    return colors[designation] || 'bg-gray-100 text-gray-800 dark:bg-gray-700 dark:text-gray-300';
  };

  const departments = [...new Set(mockTeachers.map(t => t.department))];
  const designations = [...new Set(mockTeachers.map(t => t.designation))];

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900">
      <Helmet>
        <title>{t('teachers.teachers')} - School Management System</title>
        <meta name="description" content="Meet our dedicated and qualified teaching staff committed to educational excellence" />
      </Helmet>

      <div className="container-custom py-8">
        {/* Header */}
        <motion.div
          initial={{ y: -20, opacity: 0 }}
          animate={{ y: 0, opacity: 1 }}
          transition={{ duration: 0.6 }}
          className="text-center mb-8"
        >
          <h1 className="text-4xl font-bold text-gray-900 dark:text-white mb-4">
            {t('teachers.teachersList')}
          </h1>
          <p className="text-xl text-gray-600 dark:text-gray-400 max-w-2xl mx-auto">
            Meet our dedicated and qualified teaching staff committed to educational excellence
          </p>
        </motion.div>

        {/* Search and Filters */}
        <motion.div
          initial={{ y: 20, opacity: 0 }}
          animate={{ y: 0, opacity: 1 }}
          transition={{ delay: 0.2, duration: 0.6 }}
          className="bg-white dark:bg-gray-800 rounded-xl shadow-soft p-6 mb-8"
        >
          {/* Search Bar */}
          <form onSubmit={handleSearch} className="flex gap-4 mb-4">
            <div className="flex-1 relative">
              <FiSearch className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-5 h-5" />
              <input
                type="text"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                placeholder={`${t('common.search')} ${t('teachers.teachers').toLowerCase()}...`}
                className="form-input pl-10 w-full"
              />
            </div>
            <button
              type="submit"
              className="btn btn-primary px-6"
            >
              {t('common.search')}
            </button>
            <button
              type="button"
              onClick={() => setShowFilters(!showFilters)}
              className="btn btn-outline px-6"
            >
              <FiFilter className="w-5 h-5 mr-2" />
              {t('common.filter')}
            </button>
          </form>

          {/* Filters */}
          {showFilters && (
            <motion.div
              initial={{ height: 0, opacity: 0 }}
              animate={{ height: 'auto', opacity: 1 }}
              exit={{ height: 0, opacity: 0 }}
              className="border-t border-gray-200 dark:border-gray-700 pt-4"
            >
              <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                <div>
                  <label className="form-label">{t('teachers.department')}</label>
                  <select
                    value={filters.department}
                    onChange={(e) => handleFilterChange('department', e.target.value)}
                    className="form-input"
                  >
                    <option value="">All Departments</option>
                    {departments.map(dept => (
                      <option key={dept} value={dept}>{dept}</option>
                    ))}
                  </select>
                </div>

                <div>
                  <label className="form-label">{t('teachers.designation')}</label>
                  <select
                    value={filters.designation}
                    onChange={(e) => handleFilterChange('designation', e.target.value)}
                    className="form-input"
                  >
                    <option value="">All Designations</option>
                    {designations.map(designation => (
                      <option key={designation} value={designation}>{designation}</option>
                    ))}
                  </select>
                </div>

                <div className="flex items-end">
                  <button
                    onClick={clearAllFilters}
                    className="btn btn-ghost w-full"
                  >
                    {t('common.clear')} {t('common.filter')}
                  </button>
                </div>
              </div>
            </motion.div>
          )}
        </motion.div>

        {/* Teachers Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
          {displayTeachers.map((teacher, index) => (
            <motion.div
              key={teacher._id}
              initial={{ y: 50, opacity: 0 }}
              animate={{ y: 0, opacity: 1 }}
              transition={{ delay: index * 0.1, duration: 0.6 }}
              className="bg-white dark:bg-gray-800 rounded-xl shadow-soft hover:shadow-soft-lg transition-all duration-300 overflow-hidden group"
            >
              {/* Teacher Photo */}
              <div className="relative h-48 bg-gradient-to-br from-primary-400 to-primary-600">
                {teacher.user.avatar?.url ? (
                  <img 
                    src={teacher.user.avatar.url} 
                    alt={teacher.user.name}
                    className="w-full h-full object-cover"
                  />
                ) : (
                  <div className="w-full h-full flex items-center justify-center">
                    <div className="w-20 h-20 bg-white rounded-full flex items-center justify-center">
                      <FiUser className="w-10 h-10 text-primary-600" />
                    </div>
                  </div>
                )}
                <div className="absolute top-4 right-4">
                  <span className={`badge ${getDesignationColor(teacher.designation)}`}>
                    {teacher.designation}
                  </span>
                </div>
              </div>

              {/* Teacher Info */}
              <div className="p-6">
                <h3 className="text-xl font-bold text-gray-900 dark:text-white mb-2">
                  {teacher.user.name}
                </h3>

                <div className="flex items-center text-primary-600 dark:text-primary-400 mb-3">
                  <FiBook className="w-4 h-4 mr-2" />
                  <span className="text-sm font-medium">{teacher.department}</span>
                </div>

                {/* Subjects */}
                <div className="mb-4">
                  <p className="text-sm text-gray-600 dark:text-gray-400 mb-2">
                    {t('teachers.subjects')}:
                  </p>
                  <div className="flex flex-wrap gap-1">
                    {teacher.subjects.slice(0, 2).map((subject, idx) => (
                      <span key={idx} className="badge badge-primary text-xs">
                        {subject}
                      </span>
                    ))}
                    {teacher.subjects.length > 2 && (
                      <span className="badge bg-gray-100 text-gray-600 dark:bg-gray-700 dark:text-gray-400 text-xs">
                        +{teacher.subjects.length - 2} more
                      </span>
                    )}
                  </div>
                </div>

                {/* Experience & Qualification */}
                <div className="space-y-2 mb-4">
                  <div className="flex items-center text-sm text-gray-600 dark:text-gray-400">
                    <FiAward className="w-4 h-4 mr-2" />
                    <span>{teacher.experience.totalYears} years experience</span>
                  </div>
                  <div className="flex items-center text-sm text-gray-600 dark:text-gray-400">
                    <FiAward className="w-4 h-4 mr-2" />
                    <span>{teacher.qualifications[0]?.degree}</span>
                  </div>
                  <div className="flex items-center text-sm text-gray-600 dark:text-gray-400">
                    <FiCalendar className="w-4 h-4 mr-2" />
                    <span>Joined {new Date(teacher.joiningDate).getFullYear()}</span>
                  </div>
                </div>

                {/* Contact Info */}
                <div className="border-t border-gray-200 dark:border-gray-700 pt-4">
                  <div className="flex items-center justify-between">
                    <div className="flex space-x-3">
                      <a 
                        href={`mailto:${teacher.user.email}`}
                        className="text-gray-400 hover:text-primary-600 dark:hover:text-primary-400 transition-colors"
                      >
                        <FiMail className="w-4 h-4" />
                      </a>
                      <a 
                        href={`tel:${teacher.user.phone}`}
                        className="text-gray-400 hover:text-primary-600 dark:hover:text-primary-400 transition-colors"
                      >
                        <FiPhone className="w-4 h-4" />
                      </a>
                    </div>
                    <span className="text-xs text-gray-500 dark:text-gray-400">
                      ID: {teacher.teacherId}
                    </span>
                  </div>
                </div>
              </div>
            </motion.div>
          ))}
        </div>

        {/* No Results */}
        {displayTeachers.length === 0 && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            className="text-center py-12"
          >
            <p className="text-gray-500 dark:text-gray-400 text-lg">
              No teachers found matching your criteria.
            </p>
          </motion.div>
        )}

        {/* Statistics */}
        <motion.div
          initial={{ y: 50, opacity: 0 }}
          animate={{ y: 0, opacity: 1 }}
          transition={{ delay: 0.4, duration: 0.6 }}
          className="mt-12 bg-white dark:bg-gray-800 rounded-xl shadow-soft p-8"
        >
          <h2 className="text-2xl font-bold text-gray-900 dark:text-white mb-6 text-center">
            Teaching Staff Statistics
          </h2>
          <div className="grid grid-cols-2 md:grid-cols-4 gap-6">
            <div className="text-center">
              <div className="text-3xl font-bold text-primary-600 dark:text-primary-400 mb-2">
                {mockTeachers.length}
              </div>
              <div className="text-gray-600 dark:text-gray-400">Total Teachers</div>
            </div>
            <div className="text-center">
              <div className="text-3xl font-bold text-green-600 dark:text-green-400 mb-2">
                {departments.length}
              </div>
              <div className="text-gray-600 dark:text-gray-400">Departments</div>
            </div>
            <div className="text-center">
              <div className="text-3xl font-bold text-blue-600 dark:text-blue-400 mb-2">
                {Math.round(mockTeachers.reduce((acc, t) => acc + t.experience.totalYears, 0) / mockTeachers.length)}
              </div>
              <div className="text-gray-600 dark:text-gray-400">Avg. Experience</div>
            </div>
            <div className="text-center">
              <div className="text-3xl font-bold text-purple-600 dark:text-purple-400 mb-2">
                {mockTeachers.filter(t => t.personalInfo.gender === 'Female').length}
              </div>
              <div className="text-gray-600 dark:text-gray-400">Female Teachers</div>
            </div>
          </div>
        </motion.div>
      </div>
    </div>
  );
};

export default TeachersPage;
