import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useTranslation } from 'react-i18next';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  FiPlus, 
  FiEdit, 
  FiTrash2, 
  FiCalendar, 
  FiClock, 
  FiBook, 
  FiMapPin,
  FiX,
  FiSave,
  FiEye,
  FiFilter,
  FiSearch,
  FiDownload,
  FiCopy
} from 'react-icons/fi';
import { fetchRoutines, createRoutine, updateRoutine, deleteRoutine } from '../../store/slices/routineSlice';
import { fetchActiveClasses } from '../../store/slices/classSlice';
import LoadingSpinner from '../common/LoadingSpinner';

const RoutinesManager = () => {
  const { t, i18n } = useTranslation();
  const dispatch = useDispatch();
  const currentLang = i18n.language;

  const { routines, loading, error } = useSelector(state => state.routines);
  const { activeClasses } = useSelector(state => state.classes);

  const [showModal, setShowModal] = useState(false);
  const [editingRoutine, setEditingRoutine] = useState(null);
  const [selectedRoutine, setSelectedRoutine] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [filterClass, setFilterClass] = useState('');
  const [filterSection, setFilterSection] = useState('');

  const [formData, setFormData] = useState({
    title: { en: '', bn: '' },
    class: { en: '', bn: '' },
    section: '',
    academicYear: new Date().getFullYear().toString(),
    type: 'Class',
    semester: 'Annual',
    effectiveFrom: new Date().toISOString().split('T')[0],
    schedule: [],
    status: 'published',
    isActive: true
  });

  // Constants
  const daysOfWeek = [
    { en: 'Sunday', bn: 'রবিবার' },
    { en: 'Monday', bn: 'সোমবার' },
    { en: 'Tuesday', bn: 'মঙ্গলবার' },
    { en: 'Wednesday', bn: 'বুধবার' },
    { en: 'Thursday', bn: 'বৃহস্পতিবার' },
    { en: 'Friday', bn: 'শুক্রবার' },
    { en: 'Saturday', bn: 'শনিবার' }
  ];

  const subjects = [
    { en: 'Mathematics', bn: 'গণিত' },
    { en: 'English', bn: 'ইংরেজি' },
    { en: 'Bangla', bn: 'বাংলা' },
    { en: 'Physics', bn: 'পদার্থবিজ্ঞান' },
    { en: 'Chemistry', bn: 'রসায়ন' },
    { en: 'Biology', bn: 'জীববিজ্ঞান' },
    { en: 'Science', bn: 'বিজ্ঞান' },
    { en: 'Social Science', bn: 'সামাজিক বিজ্ঞান' },
    { en: 'ICT', bn: 'আইসিটি' },
    { en: 'Physical Education', bn: 'শারীরিক শিক্ষা' },
    { en: 'Islamic Studies', bn: 'ইসলাম শিক্ষা' },
    { en: 'Break', bn: 'বিরতি' }
  ];

  const timeSlots = [
    '08:00', '08:45', '09:30', '09:45', '10:30', '11:15', '12:00', '12:45', '13:30', '14:15', '15:00'
  ];

  const sections = ['A', 'B', 'C', 'D'];
  const classes = ['Class 6', 'Class 7', 'Class 8', 'Class 9', 'Class 10'];

  useEffect(() => {
    dispatch(fetchRoutines());
  }, [dispatch]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    await dispatch(createRoutine(formData));
    setShowModal(false);
  };

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <h2 className="text-2xl font-bold">Routines Management</h2>
        <button onClick={() => setShowModal(true)} className="btn-primary">
          <FiPlus /> Add Routine
        </button>
      </div>

      <div className="grid gap-4">
        {routines.map((routine) => (
          <div key={routine._id} className="bg-white p-4 rounded-lg shadow">
            <h3 className="font-semibold">{routine.title?.en}</h3>
            <p>{routine.class?.en} - {routine.section}</p>
          </div>
        ))}
      </div>

      {showModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center">
          <div className="bg-white p-6 rounded-lg w-96">
            <h3 className="text-xl font-bold mb-4">Add Routine</h3>
            <form onSubmit={handleSubmit}>
              <input
                type="text"
                placeholder="Title (English)"
                value={formData.title.en}
                onChange={(e) => setFormData({
                  ...formData,
                  title: { ...formData.title, en: e.target.value }
                })}
                className="w-full p-2 border rounded mb-2"
              />
              <button type="submit" className="btn-primary mr-2">Save</button>
              <button onClick={() => setShowModal(false)} className="btn-secondary">Cancel</button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default RoutinesManager;
