import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useTranslation } from 'react-i18next';
import { motion, AnimatePresence } from 'framer-motion';
import { toast } from 'react-hot-toast';
import {
  FiPlus,
  FiEdit2,
  FiTrash2,
  FiUser,
  FiMail,
  FiPhone,
  FiCalendar,
  FiUpload,
  FiX,
  FiSave,
  FiEye
} from 'react-icons/fi';

import {
  fetchManagementCommittee,
  createManagementCommitteeMember,
  updateManagementCommitteeMember,
  deleteManagementCommitteeMember,
  clearError
} from '../../store/slices/managementCommitteeSlice';

const ManagementCommitteeManager = () => {
  const dispatch = useDispatch();
  const { t, i18n } = useTranslation();
  const currentLang = i18n.language;

  const {
    members,
    loading,
    createLoading,
    updateLoading,
    deleteLoading,
    error
  } = useSelector(state => state.managementCommittee);

  const [showModal, setShowModal] = useState(false);
  const [editingMember, setEditingMember] = useState(null);
  const [formData, setFormData] = useState({
    name: { en: '', bn: '' },
    position: { en: '', bn: '' },
    qualification: { en: '', bn: '' },
    experience: { en: '', bn: '' },
    bio: { en: '', bn: '' },
    contactInfo: {
      phone: '',
      email: '',
      address: { en: '', bn: '' }
    },
    joinDate: '',
    category: 'leadership',
    priority: 0,
    socialLinks: {
      facebook: '',
      linkedin: '',
      twitter: ''
    },
    photo: null
  });
  const [photoPreview, setPhotoPreview] = useState(null);

  useEffect(() => {
    dispatch(fetchManagementCommittee());
  }, [dispatch]);

  useEffect(() => {
    if (error) {
      toast.error(error);
      dispatch(clearError());
    }
  }, [error, dispatch]);

  const handleInputChange = (field, value, lang = null) => {
    if (lang) {
      setFormData(prev => ({
        ...prev,
        [field]: {
          ...prev[field],
          [lang]: value
        }
      }));
    } else if (field.includes('.')) {
      const [parent, child, subChild] = field.split('.');
      if (subChild) {
        setFormData(prev => ({
          ...prev,
          [parent]: {
            ...prev[parent],
            [child]: {
              ...prev[parent][child],
              [subChild]: value
            }
          }
        }));
      } else {
        setFormData(prev => ({
          ...prev,
          [parent]: {
            ...prev[parent],
            [child]: value
          }
        }));
      }
    } else {
      setFormData(prev => ({
        ...prev,
        [field]: value
      }));
    }
  };

  const handlePhotoChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setFormData(prev => ({ ...prev, photo: file }));
      const reader = new FileReader();
      reader.onloadend = () => {
        setPhotoPreview(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const resetForm = () => {
    setFormData({
      name: { en: '', bn: '' },
      position: { en: '', bn: '' },
      qualification: { en: '', bn: '' },
      experience: { en: '', bn: '' },
      bio: { en: '', bn: '' },
      contactInfo: {
        phone: '',
        email: '',
        address: { en: '', bn: '' }
      },
      joinDate: '',
      category: 'leadership',
      priority: 0,
      socialLinks: {
        facebook: '',
        linkedin: '',
        twitter: ''
      },
      photo: null
    });
    setPhotoPreview(null);
    setEditingMember(null);
  };

  const handleEdit = (member) => {
    setEditingMember(member);
    setFormData({
      name: member.name || { en: '', bn: '' },
      position: member.position || { en: '', bn: '' },
      qualification: member.qualification || { en: '', bn: '' },
      experience: member.experience || { en: '', bn: '' },
      bio: member.bio || { en: '', bn: '' },
      contactInfo: member.contactInfo || {
        phone: '',
        email: '',
        address: { en: '', bn: '' }
      },
      joinDate: member.joinDate ? member.joinDate.split('T')[0] : '',
      category: member.category || 'leadership',
      priority: member.priority || 0,
      socialLinks: member.socialLinks || {
        facebook: '',
        linkedin: '',
        twitter: ''
      },
      photo: null
    });
    setPhotoPreview(member.photo?.url || null);
    setShowModal(true);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      if (editingMember) {
        await dispatch(updateManagementCommitteeMember({
          id: editingMember._id,
          memberData: formData
        })).unwrap();
        toast.success(currentLang === 'bn' ? 'সফলভাবে আপডেট হয়েছে' : 'Updated successfully');
      } else {
        await dispatch(createManagementCommitteeMember(formData)).unwrap();
        toast.success(currentLang === 'bn' ? 'সফলভাবে তৈরি হয়েছে' : 'Created successfully');
      }

      setShowModal(false);
      resetForm();
    } catch (error) {
      toast.error(error || 'An error occurred');
    }
  };

  const handleDelete = async (id) => {
    if (window.confirm(currentLang === 'bn' ? 'আপনি কি নিশ্চিত?' : 'Are you sure?')) {
      try {
        await dispatch(deleteManagementCommitteeMember(id)).unwrap();
        toast.success(currentLang === 'bn' ? 'সফলভাবে মুছে ফেলা হয়েছে' : 'Deleted successfully');
      } catch (error) {
        toast.error(error || 'Failed to delete');
      }
    }
  };

  const categories = [
    { value: 'leadership', label: currentLang === 'bn' ? 'নেতৃত্ব' : 'Leadership' },
    { value: 'academic', label: currentLang === 'bn' ? 'একাডেমিক' : 'Academic' },
    { value: 'administration', label: currentLang === 'bn' ? 'প্রশাসন' : 'Administration' },
    { value: 'finance', label: currentLang === 'bn' ? 'অর্থ' : 'Finance' }
  ];

  return (
    <div className="p-6">
      {/* Header */}
      <div className="flex justify-between items-center mb-6">
        <div>
          <h1 className="text-3xl font-bold text-gray-900 dark:text-white">
            {currentLang === 'bn' ? 'পরিচালনা কমিটি ব্যবস্থাপনা' : 'Management Committee Management'}
          </h1>
          <p className="text-gray-600 dark:text-gray-400 mt-2">
            {currentLang === 'bn' 
              ? 'পরিচালনা কমিটির সদস্যদের তথ্য যোগ, সম্পাদনা এবং মুছে ফেলুন'
              : 'Add, edit, and manage management committee members'
            }
          </p>
        </div>
        <button
          onClick={() => {
            resetForm();
            setShowModal(true);
          }}
          className="btn btn-primary flex items-center gap-2"
        >
          <FiPlus className="w-5 h-5" />
          {currentLang === 'bn' ? 'নতুন সদস্য যোগ করুন' : 'Add New Member'}
        </button>
      </div>

      {/* Members Grid */}
      {loading ? (
        <div className="flex justify-center py-12">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-600"></div>
        </div>
      ) : (
        <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-6">
          {members.map((member) => (
            <motion.div
              key={member._id}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              className="bg-white dark:bg-gray-800 rounded-xl shadow-soft p-6 hover:shadow-lg transition-shadow duration-300"
            >
              <div className="flex items-start justify-between mb-4">
                <div className="flex items-center">
                  {member.photo?.url ? (
                    <img
                      src={member.photo.url}
                      alt={member.name[currentLang]}
                      className="w-16 h-16 rounded-full object-cover"
                    />
                  ) : (
                    <div className="w-16 h-16 bg-primary-100 dark:bg-primary-900 rounded-full flex items-center justify-center">
                      <FiUser className="w-8 h-8 text-primary-600 dark:text-primary-400" />
                    </div>
                  )}
                  <div className="ml-4">
                    <h3 className="font-semibold text-gray-900 dark:text-white">
                      {member.name[currentLang] || member.name.en}
                    </h3>
                    <p className="text-sm text-primary-600 dark:text-primary-400">
                      {member.position[currentLang] || member.position.en}
                    </p>
                  </div>
                </div>
                <div className="flex gap-2">
                  <button
                    onClick={() => handleEdit(member)}
                    className="p-2 text-blue-600 hover:bg-blue-50 dark:hover:bg-blue-900 rounded-lg transition-colors"
                  >
                    <FiEdit2 className="w-4 h-4" />
                  </button>
                  <button
                    onClick={() => handleDelete(member._id)}
                    className="p-2 text-red-600 hover:bg-red-50 dark:hover:bg-red-900 rounded-lg transition-colors"
                    disabled={deleteLoading}
                  >
                    <FiTrash2 className="w-4 h-4" />
                  </button>
                </div>
              </div>

              <div className="space-y-2 text-sm text-gray-600 dark:text-gray-400">
                <div className="flex items-center">
                  <FiMail className="w-4 h-4 mr-2" />
                  {member.contactInfo.email}
                </div>
                <div className="flex items-center">
                  <FiPhone className="w-4 h-4 mr-2" />
                  {member.contactInfo.phone}
                </div>
                <div className="flex items-center">
                  <FiCalendar className="w-4 h-4 mr-2" />
                  {new Date(member.joinDate).toLocaleDateString()}
                </div>
              </div>

              <div className="mt-4 pt-4 border-t border-gray-200 dark:border-gray-700">
                <span className={`px-2 py-1 rounded-full text-xs font-medium ${
                  member.category === 'leadership' ? 'bg-purple-100 text-purple-800 dark:bg-purple-900 dark:text-purple-200' :
                  member.category === 'academic' ? 'bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-200' :
                  member.category === 'administration' ? 'bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200' :
                  'bg-orange-100 text-orange-800 dark:bg-orange-900 dark:text-orange-200'
                }`}>
                  {categories.find(cat => cat.value === member.category)?.label}
                </span>
              </div>
            </motion.div>
          ))}
        </div>
      )}

      {/* Modal */}
      <AnimatePresence>
        {showModal && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4"
          >
            <motion.div
              initial={{ scale: 0.95, opacity: 0 }}
              animate={{ scale: 1, opacity: 1 }}
              exit={{ scale: 0.95, opacity: 0 }}
              className="bg-white dark:bg-gray-800 rounded-xl shadow-xl w-full max-w-4xl max-h-[90vh] overflow-y-auto"
            >
              <div className="p-6 border-b border-gray-200 dark:border-gray-700">
                <div className="flex justify-between items-center">
                  <h2 className="text-2xl font-bold text-gray-900 dark:text-white">
                    {editingMember 
                      ? (currentLang === 'bn' ? 'সদস্য সম্পাদনা' : 'Edit Member')
                      : (currentLang === 'bn' ? 'নতুন সদস্য যোগ' : 'Add New Member')
                    }
                  </h2>
                  <button
                    onClick={() => {
                      setShowModal(false);
                      resetForm();
                    }}
                    className="p-2 hover:bg-gray-100 dark:hover:bg-gray-700 rounded-lg"
                  >
                    <FiX className="w-6 h-6" />
                  </button>
                </div>
              </div>

              <form onSubmit={handleSubmit} className="p-6 space-y-6">
                {/* Photo Upload */}
                <div className="flex items-center space-x-6">
                  <div className="shrink-0">
                    {photoPreview ? (
                      <img
                        src={photoPreview}
                        alt="Preview"
                        className="w-24 h-24 rounded-full object-cover"
                      />
                    ) : (
                      <div className="w-24 h-24 bg-gray-200 dark:bg-gray-700 rounded-full flex items-center justify-center">
                        <FiUser className="w-12 h-12 text-gray-400" />
                      </div>
                    )}
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                      {currentLang === 'bn' ? 'ছবি' : 'Photo'}
                    </label>
                    <input
                      type="file"
                      accept="image/*"
                      onChange={handlePhotoChange}
                      className="block w-full text-sm text-gray-500 file:mr-4 file:py-2 file:px-4 file:rounded-full file:border-0 file:text-sm file:font-semibold file:bg-primary-50 file:text-primary-700 hover:file:bg-primary-100"
                    />
                  </div>
                </div>

                {/* Name Fields */}
                <div className="grid md:grid-cols-2 gap-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                      {currentLang === 'bn' ? 'নাম (ইংরেজি)' : 'Name (English)'} *
                    </label>
                    <input
                      type="text"
                      value={formData.name.en}
                      onChange={(e) => handleInputChange('name', e.target.value, 'en')}
                      className="input-field"
                      required
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                      {currentLang === 'bn' ? 'নাম (বাংলা)' : 'Name (Bengali)'} *
                    </label>
                    <input
                      type="text"
                      value={formData.name.bn}
                      onChange={(e) => handleInputChange('name', e.target.value, 'bn')}
                      className="input-field"
                      required
                    />
                  </div>
                </div>

                {/* Position Fields */}
                <div className="grid md:grid-cols-2 gap-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                      {currentLang === 'bn' ? 'পদবি (ইংরেজি)' : 'Position (English)'} *
                    </label>
                    <input
                      type="text"
                      value={formData.position.en}
                      onChange={(e) => handleInputChange('position', e.target.value, 'en')}
                      className="input-field"
                      required
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                      {currentLang === 'bn' ? 'পদবি (বাংলা)' : 'Position (Bengali)'} *
                    </label>
                    <input
                      type="text"
                      value={formData.position.bn}
                      onChange={(e) => handleInputChange('position', e.target.value, 'bn')}
                      className="input-field"
                      required
                    />
                  </div>
                </div>

                {/* Contact Info */}
                <div className="grid md:grid-cols-2 gap-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                      {currentLang === 'bn' ? 'ইমেইল' : 'Email'} *
                    </label>
                    <input
                      type="email"
                      value={formData.contactInfo.email}
                      onChange={(e) => handleInputChange('contactInfo.email', e.target.value)}
                      className="input-field"
                      required
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                      {currentLang === 'bn' ? 'ফোন' : 'Phone'} *
                    </label>
                    <input
                      type="tel"
                      value={formData.contactInfo.phone}
                      onChange={(e) => handleInputChange('contactInfo.phone', e.target.value)}
                      className="input-field"
                      required
                    />
                  </div>
                </div>

                {/* Category and Join Date */}
                <div className="grid md:grid-cols-3 gap-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                      {currentLang === 'bn' ? 'বিভাগ' : 'Category'} *
                    </label>
                    <select
                      value={formData.category}
                      onChange={(e) => handleInputChange('category', e.target.value)}
                      className="input-field"
                      required
                    >
                      {categories.map(cat => (
                        <option key={cat.value} value={cat.value}>
                          {cat.label}
                        </option>
                      ))}
                    </select>
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                      {currentLang === 'bn' ? 'যোগদানের তারিখ' : 'Join Date'} *
                    </label>
                    <input
                      type="date"
                      value={formData.joinDate}
                      onChange={(e) => handleInputChange('joinDate', e.target.value)}
                      className="input-field"
                      required
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                      {currentLang === 'bn' ? 'অগ্রাধিকার' : 'Priority'}
                    </label>
                    <input
                      type="number"
                      value={formData.priority}
                      onChange={(e) => handleInputChange('priority', parseInt(e.target.value) || 0)}
                      className="input-field"
                      min="0"
                    />
                  </div>
                </div>

                {/* Bio Fields */}
                <div className="grid md:grid-cols-2 gap-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                      {currentLang === 'bn' ? 'জীবনী (ইংরেজি)' : 'Bio (English)'} *
                    </label>
                    <textarea
                      value={formData.bio.en}
                      onChange={(e) => handleInputChange('bio', e.target.value, 'en')}
                      className="input-field"
                      rows={4}
                      required
                    />
                  </div>
                  <div>
                    <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                      {currentLang === 'bn' ? 'জীবনী (বাংলা)' : 'Bio (Bengali)'} *
                    </label>
                    <textarea
                      value={formData.bio.bn}
                      onChange={(e) => handleInputChange('bio', e.target.value, 'bn')}
                      className="input-field"
                      rows={4}
                      required
                    />
                  </div>
                </div>

                {/* Submit Button */}
                <div className="flex justify-end space-x-4 pt-6 border-t border-gray-200 dark:border-gray-700">
                  <button
                    type="button"
                    onClick={() => {
                      setShowModal(false);
                      resetForm();
                    }}
                    className="btn btn-secondary"
                  >
                    {currentLang === 'bn' ? 'বাতিল' : 'Cancel'}
                  </button>
                  <button
                    type="submit"
                    disabled={createLoading || updateLoading}
                    className="btn btn-primary flex items-center gap-2"
                  >
                    {(createLoading || updateLoading) ? (
                      <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white"></div>
                    ) : (
                      <FiSave className="w-4 h-4" />
                    )}
                    {editingMember 
                      ? (currentLang === 'bn' ? 'আপডেট করুন' : 'Update')
                      : (currentLang === 'bn' ? 'সংরক্ষণ করুন' : 'Save')
                    }
                  </button>
                </div>
              </form>
            </motion.div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
};

export default ManagementCommitteeManager;
